INSERT INTO `companies` (`company_id`, `user_id`, `company_name`, `company_description`, `company_website`, `is_blocked`) VALUES
(6, 1028, 'Guilde Impériale', 'Autorité suprême coordonnant toutes les autres guildes. Elle assure la direction, la stabilité de l’Empire, et gère les décisions stratégiques majeures pour le bien commun.', 'https://inperial.empire-guilde.emp/', 0),
(7, 1029, 'Guilde des Aventuriers', 'Spécialisée dans l\'exploration, la défense de l\'Empire, et les quêtes périlleuses. Offre des missions de protection, d\'exploration de territoires inconnus, et de chasse aux monstres.', 'https://aventuriers.empire-guilde.emp/', 0),
(8, 1030, 'Guilde de Mages', 'Maîtres des arcanes et de la magie, ils protègent et développent le savoir magique de l\'Empire. Offre des quêtes de recherche d\'artefacts magiques, de création de sorts puissants, et d\'expérimentation mystique.', 'https://mages.empire-guilde.emp/', 0),
(9, 1031, 'Guilde des Artisans', 'Se consacre à la création d\'armes, armures, potions, et divers objets magiques. Offre des quêtes de fabrication sur commande, réparation d\'équipements, et développement de nouvelles technologies.', 'https://artisans.empire-guilde.emp/', 0),
(10, 1032, 'Guilde des Marchands', 'Maîtres du commerce et des échanges. Propose des missions d\'échange de biens à travers l\'Empire, d\'organisation de foires, et de négociations commerciales.', 'https://marchands.empire-guilde.emp/', 0),
(11, 1033, 'Guilde des Érudits', 'Regroupe les mages, alchimistes, et savants de l\'Empire. Offre des quêtes liées à la recherche de nouveaux sorts, l\'analyse de reliques anciennes, et l\'expérimentation magique.', 'https://erudits.empire-guilde.emp/', 0),
(12, 1034, 'Guilde des Diplomates', 'Spécialisée dans la gestion des relations entre les différentes factions et peuples de l\'Empire. Offre des quêtes de négociation de traités, missions diplomatiques, et résolution de conflits politiques.', 'https://diplomates.empire-guilde.emp/', 0),
(13, 1035, 'Guilde des Assassins', 'Opère dans l\'ombre pour maintenir l\'ordre au sein de l\'Empire. Offre des quêtes secrètes, d\'élimination de cibles nuisibles, et d\'infiltration dans des organisations ennemies.', 'https://assassins.empire-guilde.emp/', 0),
(14, 1036, 'Guilde des Mécaniciens', 'Experts en technologies avancées et ingénierie. Offre des quêtes de réparation de machines, construction de fortifications, et développement d\'innovations technologiques.', 'https://mecaniciens.empire-guilde.emp/', 0),
(15, 1037, 'Guilde des Soigneurs', 'Composée de prêtres, guérisseurs et médecins. Offre des quêtes de soin aux blessés, préparation de potions curatives, et soutien médical sur le terrain.', 'https://soigneurs.empire-guilde.emp/', 0),
(16, 1038, 'Guilde des Espions', 'Spécialisée dans la collecte d\'informations et l\'espionnage. Propose des quêtes de surveillance, infiltration, et collecte de renseignements pour prévenir les menaces à l\'Empire.', 'https://espions.empire-guilde.emp/', 0),
(17, 1039, 'Guilde des Chasseurs', 'Regroupe ceux qui chassent pour la survie de l\'Empire. Offre des quêtes de chasse à des créatures dangereuses, récolte de peaux et de matériaux rares, et exploration de régions sauvages.', 'https://chasseurs.empire-guilde.emp/', 0),
(18, 1040, 'Guilde des Cartographes', 'Se consacre à la cartographie de nouvelles régions de l\'Empire. Offre des quêtes d\'exploration et de création de cartes précises des territoires inconnus.', 'https://cartographes.empire-guilde.emp/', 0),
(19, 1041, 'Guilde des Protecteurs', 'Regroupe les défenseurs des villes et des terres de l\'Empire. Propose des quêtes de défense de villages, escortes de personnalités importantes, et gestion des milices locales.', 'https://protecteurs.empire-guilde.emp/', 0),
(20, 1042, 'Guilde des Transporteurs', 'Se charge de la circulation des biens et des personnes à travers l\'Empire. Offre des quêtes d\'escorte de convois, sécurisation de routes commerciales, et transport de biens précieux.', 'https://transporteurs.empire-guilde.emp/', 0);
